mapper.update:
UPDATE tbl_news_info 
SET reviewState=#{curStats} 
WHERE id=#{newsId}

UPDATE tbl_news_info 
SET title=#{title},content=#{content}, createDate=#{createDate}, reviewState=#{reviewState} 
WHERE id=#{id}

mapper.select:
SELECT *
FROM tbl_news_info
WHERE reviewState =#{reviewState}
LIMIT #{page},#{size}

SELECT *
FROM tbl_news_info
WHERE reviewState=#{reviewState}

	OR reviewState=3

ORDER By ${sort} ${order}
LIMIT ${offset} ,${limit}

SELECT count(1)
FROM tbl_news_info
WHERE reviewState=#{reviewState}

	OR reviewState=3

ORDER By ${sort} ${order}

SELECT count(*) 
FROM tbl_news_info 
WHERE TYPE = #{type}

SELECT * 
FROM tbl_news_info 
WHERE TYPE = #{type} 
LIMIT #{index},#{currentCount}

SELECT * 
FROM tbl_news_info 
WHERE TYPE = #{type} LIMIT #{index},#{currentCount}
