FROM tbl_product_info
WHERE reviewState=#{reviewState}
ORDER BY id DESC LIMIT #{page},#{size}

SELECT *
FROM tbl_product_info
ORDER By ${sort} ${order}
limit  ${offset} ,${limit}

SELECT *
FROM tbl_product_info
WHERE reviewState=#{reviewState}

	OR reviewState=3

ORDER By ${sort} ${order}
limit ${offset} ,${limit}

SELECT count(1)
FROM tbl_product_info
WHERE reviewState=#{reviewState}

	OR reviewState=3

ORDER By ${sort} ${order}

SELECT count(1)
FROM tbl_product_info
ORDER By ${sort} ${order}

       UPDATE tbl_product_info
		SET  OffProductInfo=#{arg1}
